// TermsModal.jsx
import React, { useState } from 'react';
import '../styles/TermsModal.css'; // נפרד לקובץ CSS כדי לשמור על סגנון נקי

const TermsModal = () => {
  const [show, setShow] = useState(false);

  const openModal = () => setShow(true);
  const closeModal = () => setShow(false);

  return (
    <>
      {/* כפתור קבוע לתחתית הדף */}
      <button className="terms-button" onClick={openModal}>
        תקנון שימוש באתר
      </button>

      {show && (
        <div className="modal-overlay" onClick={closeModal}>
          <div
            className="modal-content-term"
            onClick={(e) => e.stopPropagation()} // כדי שהקליקים בתוך המודל לא יסגרו אותו
          >
            <h2>תקנון שימוש באתר</h2>
            <p>
              גלישה ושימוש באתר מהווים הסכמה מלאה לכל התנאים המפורטים במסמך זה.
              אנו ממליצים לעיין בו ביסודיות לפני השימוש.
            </p>
            <p>
              האתר מיועד לשמש כפלטפורמה לפרסום מודעות להשכרת דירות ולהחלפה,
              ומיועד אך ורק עבור המגזר החרדי. המידע המפורסם בו מוזן על ידי
              המשתמשים עצמם, והאחריות לתוכנו חלה על המפרסם בלבד. כל שימוש במידע
              ו/או התקשרות עם מפרסם – באחריות המשתמש בלבד.
            </p>
            <p>
              האתר אינו אחראי לכל נזק, הפסד או תקלה שעלולים להיגרם כתוצאה משימוש
              בשירותיו או מהסתמכות על המידע המופיע בו.
            </p>
            <p>
              המשתמש מתחייב שלא לבצע איסוף נתונים מהאתר בכל אמצעי, למעט צפייה
              רגילה במודעות, כפי שמקובל בגלישה סבירה. חל איסור להשתמש בכלים
              אוטומטיים או טכנולוגיים לצורך כך, לרבות רובוטים, סורקים (spiders),
              scrapers ודומיהם.
            </p>
            <p>
              כל פנייה למפרסמי המודעות שאינה לצורך אמיתי של שכירות או החלפה –
              מהווה הפרה של תנאי השימוש, והחברה שומרת לעצמה את הזכות לפעול
              משפטית ולדרוש פיצוי בגין נזקים שייגרמו.
            </p>
            <p>
              האתר עשוי לשלוח, מעת לעת, עדכונים והודעות פרסומיות למשתמשים.
              פרסום מודעה באתר מהווה אישור לכך מצד המפרסם.
            </p>
            <p>
              הנהלת האתר רשאית לשנות ולעדכן את תקנון השימוש בכל עת. המשך שימוש
              באתר לאחר ביצוע עדכונים ייחשב כהסכמה לתנאים החדשים.
            </p>

            <button className="modal-close" onClick={closeModal}>
              סגור
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default TermsModal;
